mapper.insert:
    	INSERT INTO BOARD(
			SEQ
			, TITLE
			, SUBTITLE
			, SUMMARY
			, CONTENT
			, AUTHOR
			, REG_DATE
    	)
    	VALUES(
 			#{seq}
    		, #{title}
    		, #{subtitle}
    		, #{summary}
    		, #{content}
    		, #{author}
    		, NOW()
    	)

mapper.update:
UPDATE BOARD SET
	TITLE 				= #{title} 
	, SUBTITLE 			= #{subtitle} 
	, SUMMARY 			= #{summary} 
	, CONTENT 			= #{content}
	, AUTHOR 			= #{author} 
WHERE SEQ = #{seq}
