mapper.insert:
INSERT INTO FILE(
	CHANNEL
	, CATEGORY
	, OWNER_SEQ
	, NAME
	, ORIGINAL_NAME
	, FILE_SIZE
	, PATH
	, TITLE
	, REG_IP
	, REG_ID
	, REG_DATE
)
VALUES(
	#{channel}
	, #{category}
	, #{ownerSeq}
	, #{name}
	, #{originalName}
	, #{fileSize}
	, #{path}
	, #{title}
	, #{regIp}
	, #{regId}
	, NOW()
)

mapper.select:
SELECT SEQ AS "seq" , CATEGORY AS "category" , CHANNEL AS "channel" , OWNER_SEQ AS "ownerSeq" , NAME AS "name" , ORIGINAL_NAME AS "originalName" , FILE_SIZE AS "fileSize" , TITLE AS "title" , PATH AS "path" , REG_IP AS "regIp" , REG_ID AS "regId" , REG_DATE AS "regDate" , MOD_IP AS "modIp" , MOD_ID AS "modId" , MOD_DATE AS "modDate" FROM FILE WHERE 1 = 1
AND CHANNEL = #{channel}
AND CATEGORY = #{category}
AND CATEGORY LIKE CONCAT(CONCAT('%',#{attachment}),'%')
AND OWNER_SEQ = #{ownerSeq}
ORDER BY REG_DATE ASC ORDER BY REG_DATE DESC
ORDER BY REG_DATE ASC
